                        , "date_start" = in_date_start
                        , "uuid" = in_uuid
                        , "path" = in_path
                        , "active" = in_active
                        , "date_created" = in_date_created
                        , "data" = in_data
                        , "description" = in_description
                    WHERE 1=1
                    AND "uuid" = in_uuid
                    ;
                    _id := 1;
                END;
            END IF;
        END;
        BEGIN
            --INSERT
            IF (_countItems = 0 AND in_set_type != 'updateonly') THEN 			
                BEGIN			
                    INSERT INTO "content_item"
                    (
                        "status"
                        , "type_id"
                        , "code"
                        , "display_name"
                        , "name"
                        , "date_modified"
                        , "date_end"
                        , "date_start"
                        , "uuid"
                        , "path"
                        , "active"
                        , "date_created"
                        , "data"
                        , "description"
                    )
                    VALUES
                    (
                        in_status
                        , in_type_id
                        , in_code
                        , in_display_name
                        , in_name
                        , in_date_modified
                        , in_date_end
                        , in_date_start
                        , in_uuid
                        , in_path
                        , in_active
                        , in_date_created
                        , in_data
                        , in_description
                    )
                    ;
                    _id := 1;                  
                END;
            END IF;
        END;     
        SELECT _id INTO out_id;
    END;
    RETURN;
END;
$$ LANGUAGE plpgsql;

