CREATE OR REPLACE FUNCTION usp_message_browse_filter
(
    in_page int = 1,
    in_page_size int = 10,
    in_sort VARCHAR(500) = 'date_modified ASC',
    in_filter VARCHAR(4000) = NULL
    
)
RETURNS setof "message_result"
AS $$
DECLARE
    _sql VARCHAR(4000);
BEGIN

    IF (in_page = 0) THEN
        in_page := 1;
    END IF;
    IF (in_page_size = 0) THEN
        in_page_size := 10;
    END IF;
    
    _sql := 'SELECT count(*) over () as total_rows'
    || ', "status"'
    || ', "profile_from_name"'
    || ', "date_modified"'
    || ', "read"'
    || ', "profile_from_id"'
    || ', "profile_to_token"'
    || ', "app_id"'
    || ', "profile_to_id"'
    || ', "active"'
    || ', "date_created"'
    || ', "profile_to_name"'
    || ', "subject"'
    || ', "sent"'
    || ', "uuid"'
    || ' FROM "message" WHERE 1=1 ';
    
    BEGIN
        IF in_filter IS NOT NULL AND in_filter != '' THEN
            _sql := _sql || ' ' || in_filter || ' ';
        END IF;
    END;    
    
    _sql := _sql 
    || ' GROUP BY '
    || '"status" '
    || ', "profile_from_name" '
    || ', "date_modified" '
    || ', "read" '
    || ', "profile_from_id" '
    || ', "profile_to_token" '
    || ', "app_id" '
    || ', "profile_to_id" '
    || ', "active" '
    || ', "date_created" '
    || ', "profile_to_name" '
    || ', "subject" '
    || ', "sent" '
    || ', "uuid" '
    ;
    
    _sql := _sql 
    || ' ORDER BY '
    || in_sort
    || ' LIMIT '
    || in_page_size
    || ' OFFSET '
    || (in_page_size * in_page) - in_page_size;
    
    RAISE INFO '%', _sql;
    RETURN QUERY EXECUTE _sql;
END;
$$ LANGUAGE plpgsql;

