(
    in_page int = 1,
    in_page_size int = 10,
    in_sort VARCHAR(500) = 'date_modified ASC',
    in_filter VARCHAR(4000) = NULL
    
)
RETURNS setof "game_session_result"
AS $$
DECLARE
    _sql VARCHAR(4000);
BEGIN

    IF (in_page = 0) THEN
        in_page := 1;
    END IF;
    IF (in_page_size = 0) THEN
        in_page_size := 10;
    END IF;
    
    _sql := 'SELECT count(*) over () as total_rows'
    || ', "code"'
    || ', "network_uuid"'
    || ', "profile_id"'
    || ', "game_area"'
    || ', "profile_network"'
    || ', "profile_device"'
    || ', "display_name"'
    || ', "uuid"'
    || ', "network_external_port"'
    || ', "game_players_connected"'
    || ', "status"'
    || ', "game_state"'
    || ', "hash"'
    || ', "description"'
    || ', "network_external_ip"'
    || ', "game_level"'
    || ', "profile_username"'
    || ', "active"'
    || ', "game_id"'
    || ', "game_code"'
    || ', "game_player_z"'
    || ', "game_player_x"'
    || ', "network_port"'
    || ', "game_players_allowed"'
    || ', "name"'
    || ', "date_modified"'
    || ', "game_type"'
    || ', "date_created"'
    || ', "network_ip"'
    || ', "network_use_nat"'
    || ' FROM "game_session" WHERE 1=1 ';
    
    BEGIN
        IF in_filter IS NOT NULL AND in_filter != '' THEN
            _sql := _sql || ' ' || in_filter || ' ';
        END IF;
    END;    
    
    _sql := _sql 
    || ' GROUP BY '
    || '"code" '
    || ', "network_uuid" '
    || ', "profile_id" '
    || ', "game_area" '
    || ', "profile_network" '
    || ', "profile_device" '
    || ', "display_name" '
    || ', "uuid" '
    || ', "network_external_port" '
    || ', "game_players_connected" '
    || ', "status" '
    || ', "game_state" '
    || ', "hash" '
    || ', "description" '
    || ', "network_external_ip" '
    || ', "game_level" '
    || ', "profile_username" '
    || ', "active" '
    || ', "game_id" '
    || ', "game_code" '
    || ', "game_player_z" '
    || ', "game_player_x" '
    || ', "network_port" '
    || ', "game_players_allowed" '
    || ', "name" '
    || ', "date_modified" '
    || ', "game_type" '
    || ', "date_created" '
    || ', "network_ip" '
    || ', "network_use_nat" '
    ;
    
    _sql := _sql 
    || ' ORDER BY '
    || in_sort
    || ' LIMIT '
    || in_page_size
    || ' OFFSET '
    || (in_page_size * in_page) - in_page_size;
    
    RAISE INFO '%', _sql;
    RETURN QUERY EXECUTE _sql;
END;
$$ LANGUAGE plpgsql;

