                        , "data_layer_actors" = in_data_layer_actors
                        , "active" = in_active
                        , "date_created" = in_date_created
                        , "data_layer_enemy" = in_data_layer_enemy
                        , "data" = in_data
                        , "description" = in_description
                    WHERE 1=1
                    AND "uuid" = in_uuid
                    ;
                    _id := 1;
                END;
            END IF;
        END;
        BEGIN
            --INSERT
            IF (_countItems = 0 AND in_set_type != 'updateonly') THEN 			
                BEGIN			
                    INSERT INTO "game_session_data"
                    (
                        "status"
                        , "code"
                        , "display_name"
                        , "name"
                        , "date_modified"
                        , "data_results"
                        , "uuid"
                        , "data_layer_projectile"
                        , "data_layer_actors"
                        , "active"
                        , "date_created"
                        , "data_layer_enemy"
                        , "data"
                        , "description"
