CREATE OR REPLACE FUNCTION usp_game_profile_content_browse_filter
(
    in_page int = 1,
    in_page_size int = 10,
    in_sort VARCHAR(500) = 'date_modified ASC',
    in_filter VARCHAR(4000) = NULL
    
)
RETURNS setof "game_profile_content_result"
AS $$
DECLARE
    _sql VARCHAR(4000);
BEGIN

    IF (in_page = 0) THEN
        in_page := 1;
    END IF;
    IF (in_page_size = 0) THEN
        in_page_size := 10;
    END IF;
    
    _sql := 'SELECT count(*) over () as total_rows'
    || ', "username"'
    || ', "code"'
    || ', "profile_id"'
    || ', "increment"'
    || ', "path"'
    || ', "display_name"'
    || ', "uuid"'
    || ', "platform"'
    || ', "filename"'
    || ', "source"'
    || ', "version"'
    || ', "game_network"'
    || ', "status"'
    || ', "hash"'
    || ', "description"'
    || ', "content_type"'
    || ', "active"'
    || ', "game_id"'
    || ', "data"'
    || ', "name"'
    || ', "extension"'
    || ', "date_modified"'
    || ', "date_created"'
    || ' FROM "game_profile_content" WHERE 1=1 ';
    
    BEGIN
        IF in_filter IS NOT NULL AND in_filter != '' THEN
            _sql := _sql || ' ' || in_filter || ' ';
        END IF;
    END;    
    
    _sql := _sql 
    || ' GROUP BY '
    || '"username" '
    || ', "code" '
    || ', "profile_id" '
    || ', "increment" '
    || ', "path" '
    || ', "display_name" '
    || ', "uuid" '
    || ', "platform" '
    || ', "filename" '
    || ', "source" '
    || ', "version" '
    || ', "game_network" '
    || ', "status" '
    || ', "hash" '
    || ', "description" '
    || ', "content_type" '
    || ', "active" '
    || ', "game_id" '
    || ', "data" '
    || ', "name" '
    || ', "extension" '
    || ', "date_modified" '
    || ', "date_created" '
    ;
    
    _sql := _sql 
    || ' ORDER BY '
    || in_sort
    || ' LIMIT '
    || in_page_size
    || ' OFFSET '
    || (in_page_size * in_page) - in_page_size;
    
    RAISE INFO '%', _sql;
    RETURN QUERY EXECUTE _sql;
END;
$$ LANGUAGE plpgsql;

