    || ', "latitude"'
    || ', "facebook"'
    || ', "offer_id"'
    || ', "address2"'
    || ' FROM "offer_location" WHERE 1=1 ';
    
    BEGIN
        IF in_filter IS NOT NULL AND in_filter != '' THEN
            _sql := _sql || ' ' || in_filter || ' ';
        END IF;
    END;    
    
    _sql := _sql 
    || ' GROUP BY '
    || '"status" '
    || ', "fax" '
    || ', "code" '
    || ', "description" '
    || ', "address1" '
    || ', "twitter" '
