CREATE OR REPLACE FUNCTION usp_event_location_browse_filter
(
    in_page int = 1,
    in_page_size int = 10,
    in_sort VARCHAR(500) = 'date_modified ASC',
    in_filter VARCHAR(4000) = NULL
    
)
RETURNS setof "event_location_result"
AS $$
DECLARE
    _sql VARCHAR(4000);
BEGIN

    IF (in_page = 0) THEN
        in_page := 1;
    END IF;
    IF (in_page_size = 0) THEN
        in_page_size := 10;
    END IF;
    
    _sql := 'SELECT count(*) over () as total_rows'
    || ', "status"'
    || ', "fax"'
    || ', "code"'
    || ', "description"'
    || ', "address1"'
    || ', "twitter"'
    || ', "phone"'
    || ', "postal_code"'
    || ', "country_code"'
    || ', "date_created"'
    || ', "active"'
    || ', "uuid"'
    || ', "state_province"'
    || ', "city"'
    || ', "display_name"'
    || ', "name"'
    || ', "date_modified"'
    || ', "dob"'
    || ', "date_start"'
    || ', "longitude"'
    || ', "email"'
    || ', "event_id"'
    || ', "date_end"'
    || ', "latitude"'
    || ', "facebook"'
    || ', "address2"'
    || ' FROM "event_location" WHERE 1=1 ';
    
    BEGIN
        IF in_filter IS NOT NULL AND in_filter != '' THEN
            _sql := _sql || ' ' || in_filter || ' ';
        END IF;
    END;    
    
    _sql := _sql 
    || ' GROUP BY '
    || '"status" '
    || ', "fax" '
    || ', "code" '
    || ', "description" '
    || ', "address1" '
    || ', "twitter" '
    || ', "phone" '
    || ', "postal_code" '
    || ', "country_code" '
    || ', "date_created" '
    || ', "active" '
    || ', "uuid" '
    || ', "state_province" '
    || ', "city" '
    || ', "display_name" '
    || ', "name" '
    || ', "date_modified" '
    || ', "dob" '
    || ', "date_start" '
    || ', "longitude" '
    || ', "email" '
    || ', "event_id" '
    || ', "date_end" '
    || ', "latitude" '
    || ', "facebook" '
    || ', "address2" '
    ;
    
    _sql := _sql 
    || ' ORDER BY '
    || in_sort
    || ' LIMIT '
    || in_page_size
    || ' OFFSET '
    || (in_page_size * in_page) - in_page_size;
    
    RAISE INFO '%', _sql;
    RETURN QUERY EXECUTE _sql;
END;
$$ LANGUAGE plpgsql;

