    || ', "date_created"'
    || ', "game_energy"'
    || ', "game_data"'
    || ' FROM "game_rpg_item_weapon" WHERE 1=1 ';
    
    BEGIN
        IF in_filter IS NOT NULL AND in_filter != '' THEN
            _sql := _sql || ' ' || in_filter || ' ';
        END IF;
    END;    
    
    _sql := _sql 
    || ' GROUP BY '
