    || ', "game_skill"'
    || ', "game_health"'
    || ', "date_created"'
    || ', "game_energy"'
    || ', "game_data"'
    || ' FROM "game_rpg_item_skill" WHERE 1=1 ';
    
    BEGIN
        IF in_filter IS NOT NULL AND in_filter != '' THEN
            _sql := _sql || ' ' || in_filter || ' ';
        END IF;
    END;    
    
    _sql := _sql 
    || ' GROUP BY '
    || '"status" '
    || ', "third_party_oembed" '
    || ', "code" '
    || ', "description" '
    || ', "game_defense" '
    || ', "third_party_url" '
