    , in_display_name varchar (255) = NULL
    , in_name varchar (255) = NULL
    , in_date_modified TIMESTAMP = now()
    , in_type varchar (40) = NULL
    , in_uuid uuid = NULL
    , in_key varchar (50) = NULL
    , in_game_id uuid = NULL
    , in_active boolean = NULL
    , in_date_created TIMESTAMP = now()
    , in_data varchar = NULL
    , in_order varchar (40) = NULL
    , in_description varchar (255) = NULL
    , OUT out_id int                        
)
RETURNS int
AS $$
DECLARE
    _countItems int;
    _id int;
BEGIN
    BEGIN
        _countItems := 0;
        _id := 0;
        
        BEGIN
            IF (in_set_type != 'full' AND in_set_type != 'insertonly' AND in_set_type != 'updateonly') THEN
                in_set_type := 'full';
            END IF;
        END;

	-- IF TYPE IS FULL SET (COUNT CHECK, UPDATE, INSERT)
	-- GET COUNT TO CHECK
	BEGIN
	    IF (in_set_type = 'full') THEN
                BEGIN
                    -- CHECK COUNT
                    SELECT COUNT(*) INTO _countItems
                    FROM  "game_level"  
                    WHERE 1=1
                    AND "uuid" = in_uuid
                    ;
                END;
            END IF;
	END;

        BEGIN
            -- UPDATE
            IF (_countItems > 0 AND in_set_type != 'insertonly')
                OR (_countItems = 0 AND in_set_type = 'updateonly') THEN
                BEGIN		
                    UPDATE "game_level" 
                    SET
                        "status" = in_status
                        , "sort" = in_sort
                        , "code" = in_code
                        , "display_name" = in_display_name
                        , "name" = in_name
                        , "date_modified" = in_date_modified
                        , "type" = in_type
                        , "uuid" = in_uuid
                        , "key" = in_key
                        , "game_id" = in_game_id
                        , "active" = in_active
                        , "date_created" = in_date_created
                        , "data" = in_data
                        , "order" = in_order
                        , "description" = in_description
                    WHERE 1=1
                    AND "uuid" = in_uuid
                    ;
                    _id := 1;
                END;
            END IF;
        END;
        BEGIN
            --INSERT
            IF (_countItems = 0 AND in_set_type != 'updateonly') THEN 			
                BEGIN			
                    INSERT INTO "game_level"
                    (
                        "status"
                        , "sort"
                        , "code"
                        , "display_name"
                        , "name"
                        , "date_modified"
                        , "type"
                        , "uuid"
                        , "key"
                        , "game_id"
                        , "active"
                        , "date_created"
                        , "data"
                        , "order"
                        , "description"
                    )
                    VALUES
                    (
                        in_status
                        , in_sort
                        , in_code
                        , in_display_name
                        , in_name
                        , in_date_modified
                        , in_type
                        , in_uuid
                        , in_key
                        , in_game_id
                        , in_active
                        , in_date_created
                        , in_data
                        , in_order
                        , in_description
                    )
                    ;
                    _id := 1;                  
                END;
            END IF;
        END;     
        SELECT _id INTO out_id;
    END;
    RETURN;
END;
$$ LANGUAGE plpgsql;

