CREATE OR REPLACE FUNCTION usp_game_achievement_meta_get_game_id
(
    in_game_id uuid = NULL
)
RETURNS setof "game_achievement_meta"
AS $$
DECLARE
BEGIN
    RETURN QUERY SELECT
        "status"
        , "sort"
        , "code"
        , "display_name"
        , "name"
        , "game_stat"
        , "date_modified"
        , "type"
        , "level"
        , "uuid"
        , "points"
        , "key"
        , "game_id"
        , "active"
        , "date_created"
        , "data"
        , "leaderboard"
        , "description"
    FROM "game_achievement_meta"
    WHERE 1=1
    AND "game_id" = in_game_id
    ;
    RETURN;
END;
$$ LANGUAGE plpgsql;

